  def lookup_for_file(path)
    suffix1 = (/\.(\w+)$/ =~ path && $1.downcase)
    suffix2 = (/\.(\w+)\.[\w\-]+$/ =~ path && $1.downcase)
    MIME_TYPES[suffix1] || MIME_TYPES[suffix2] || "text/plain"
  end
  
  module_function :lookup_for_file
end
